package tutttts;
import java.util.Scanner;
public class sumof5series31 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=1, s=0;
		
		
		while(a<=5)
		{   
		System.out.println("value of a"+a);
		s=s+a;
		a=a+1;
		}	
		System.out.println("sum is"+s);
	
	}

}
